import React, { Component } from 'react';
import './style.css'
import { Button, RowInput, Input } from "../../components";

class datauser extends Component {
    constructor(props) {
        super(props);
        this.state = { 
            users:[],
            addUser:{
                id:1,
                nik:'',
                jabatan:'',
                nama:'',
                username:'',
                password:'',
                alamat:'',
                roleType:'user'
            }
        }
    }

// Menangkap nilai tambah user
    onChangeInput = e => {
        let timestamp = new Date().getTime();
        let newUser = {...this.state.addUser}
        newUser['id'] = timestamp;
        newUser [e.target.name]=e.target.value
        this.setState({
            addUser: newUser
        })
    }

// Simpan
    simpanUser = () => {
        console.log(this.state.newUser);
    }

// Ambil Data Semua User
    componentDidMount() {
        fetch('http://localhost:3030/users')
            .then(response => response.json())
            .then(json => this.setState({ users: json }))
    }

    // refreshHalaman Setelah Aksi
    getPostAPI = () => {
            fetch('http://localhost:3030/users')
                .then(response => response.json())
                .then(json => this.setState({ users: json }))
    }

    // HandlerRemove
    handleRemove = (data) =>  {
        console.log(data)
        fetch(`http://localhost:3030/users/${data}`).then ((res)=>{
            this.getPostAPI()
            console.log(res);
            window.alert('Data Berhasil Dihapus !')
        })
    }

    render() { 
        // log Inputan
        console.log(this.state)
        return (
            <>
            <div className="listUser">
            <div className="container-fluid" style={{marginTop: "140px"}}>
            <h4>List Data User</h4>
            <div className="form-daftar">
                    <RowInput value={this.state.name} label="Nama" type="text" name="name" onChange={this.onChangeInput} />
                    <RowInput value={this.state.nik} label="Nama" type="text" name="nik" onChange={this.onChangeInput} />
                    <RowInput value={this.state.jabatan} label="Nama" type="text" name="jabatan" onChange={this.onChangeInput} />
                    <RowInput value={this.state.username} label="Username" type="text" name="username" onChange={this.onChangeInput} />
                    <RowInput value={this.state.password} label="Password" type="password" name="password" onChange={this.onChangeInput} />
                    <div>
                        <Input typeInput="button" valueInput="Daftar" onClickInput={this.simpanUser} />
                    </div>
                </div>
            <table className="tableUser">
                <thead className="thead-dark">
                <tr>
                    <th>No</th>
                    <th>Nik Karyawan</th>
                    <th>Username</th>
                    <th>Nama</th>
                    <th>Jabatan</th>
                    <th>Alamat</th>
                    <th>Aksi</th>
                </tr>
                </thead>
            <tbody>
            {this.state.users.map((user, idx) => {  
                return <tr key={idx}>
                            <td scope="row" >{idx+1}</td>
                            <td>{user.nik}</td>
                            <td>{user.username}</td>
                            <td>{user.nama}</td>
                            <td>{user.jabatan}</td>
                            <td>{user.alamat}</td>
                            <td> 
                                    <>
                                        <Button classButton={"btn btn-info"}>Edit</Button>
                                        <Button data={user} remove={this.handleRemove} classButton={"btn btn-danger"}>Delete</Button>
                                    </>
                            </td>
                        </tr>
                    })}
            </tbody>
        </table>
            </div>
            </div>
            </>
        )
        
                
    }
}
export default datauser;
