import React, { Component } from 'react';

class Table extends Component {
    constructor(props) {
        super(props);
        this.state = {  }
    }
    render() { 
        return ( 
            <>
            <table className="tableUser">
                <thead className="thead-dark">
                <tr>
                    <th>No</th>
                    <th>Nik Karyawan</th>
                    <th>Username</th>
                    <th>Nama</th>
                    <th>Jabatan</th>
                    <th>Alamat</th>
                    <th>Aksi</th>
                </tr>
                </thead>
            <tbody>
            {this.state.users.map((user, idx) => {  
                return <tr key={idx}>
                            <td scope="row" >{idx+1}</td>
                            <td>{user.nik}</td>
                            <td>{user.username}</td>
                            <td>{user.nama}</td>
                            <td>{user.jabatan}</td>
                            <td>{user.alamat}</td>
                            <td> 
                                    <>
                                        <button className='btn btn-info'>Detail</button>
                                        <button className='btn btn-warning'> Edit</button>
                                        <button data={user.id} onClick={this.handleRemove}className='btn btn-danger'>Delete</button>
                                    </>
                                                            
                            </td>
                        </tr>
                    })}
            </tbody>
        </table>
                



            </>
         );
    }
}
 
export default Table;
