import React, { Component } from 'react';
import { Nav, Body, Footer } from "./templates"
import "./App.css"

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      defaultPass:"123",
      defaultType: 2,
      userListAPI:[],
      isLoggedIn: false
    }
  }

  componentDidMount= async() =>{
    await fetch('https://jsonplaceholder.typicode.com/users')
        .then(response => response.json())
        .then(json => this.setState({ userListAPI: json }))
        const APIUser = this.state.userList
        this.state.userListAPI.forEach(usr => {
          let obj = {
            name: usr.name,
            email: usr.email,
            password : this.state.defaultPass,
            type : this.state.defaultType
          }
          APIUser.push(obj)
        })        
        this.setState({
          userList:APIUser
        })
        console.log(APIUser)


  }

  shouldComponentUpdate(lastProp) {
    if (lastProp.page !== this.state.page)
      return true
    return false
  }

  onClickButton = (page) => {
    this.setState({
      page
    })
  }

  changeLogIn = () => {
    this.setState(oldState => ({ isLoggedIn: !oldState.isLoggedIn }))
  }

  render() {
    console.log(this.state.page)
    return (
      <>
        <Nav
          statusLogin={this.state.isLoggedIn}
          changeLogIn={this.changeLogIn}
          changePage={this.onClickButton}
        />
        <Body
          statusLogin={this.state.isLoggedIn}
          changeLogIn={this.changeLogIn}
          page={this.state.page}
          changePage={this.onClickButton}
        />
        <Footer />
      </>
    );
  }
}

export default App;
