import React, { Component } from 'react';
import { Switch, Route } from "react-router-dom"
import { Home, Product, Login, Daftar } from "../../pages"



/*
- App
    - Header
    - Nav
    - Body
        - Home
        - About
        - Login
        - Register
    - Footer
*/


class Body extends Component {
    constructor(props) {
        super(props);
        this.state = {
            users: [],
        }
    }

    showPage = () => {
        const { changeLogIn, statusLogin } = this.props
        return (
            <Switch>
                <Route exact path="/" children={(props) => <Home {...props} statusLogin={statusLogin} />} />
                <Route path="/product" component={Product} /> {/* history */}
                <Route path="/login">
                    <Login changeLogIn={changeLogIn} listUsers={this.state.users} />
                </Route>
                <Route path="/daftar">
                    <Register listUsers={this.state.users} tambahUser={this.addUsers} />
                </Route>
            </Switch>
        )
    }

    addUsers = obj => {
        const { name, username, password } = obj
        let oldUsers = this.state.users
        oldUsers.push({
            name,
            username,
            password
        })
        this.setState({
            users: oldUsers
        })
    }

    render() {
        return (
            <>
                {
                    this.showPage()
                }
            </>
        );
    }
}

export default Body;
