import React, { Component } from 'react';
import './style.css'
import { Button } from "../../components";

class datauser extends Component {
    constructor(props) {
        super(props);
        this.state = { 
            users:[]
        }
    }

// Ambil Data Semua User
    componentDidMount() {
        fetch('http://localhost:3030/users')
            .then(response => response.json())
            .then(json => this.setState({ users: json }))
    }

    // HandlerRemove
    handleRemove = (data) =>  {
        console.log(data)
        fetch.delete('http://localhost:3030/users/${data}').then ((res)=>{
            console.log(res);
        })
    }

    render() { 
        return (
            <>
            <div className="listUser">
            <div className="container-fluid" style={{marginTop: "140px"}}>
            <h4>List Data User</h4>
            <table className="tableUser">
                <thead className="thead-dark">
                <tr>
                    <th>No</th>
                    <th>Nik Karyawan</th>
                    <th>Username</th>
                    <th>Nama</th>
                    <th>Jabatan</th>
                    <th>Alamat</th>
                    <th>Aksi</th>
                </tr>
                </thead>
            <tbody>
            {this.state.users.map((user, idx) => {  
                return <tr key={idx}>
                            <td scope="row" >{idx+1}</td>
                            <td>{user.nik}</td>
                            <td>{user.username}</td>
                            <td>{user.nama}</td>
                            <td>{user.jabatan}</td>
                            <td>{user.alamat}</td>
                            <td> 
                                    <>
                                        <Button data={user} remove={this.handleRemove} classButton={"btn btn-warning"}>Edit</Button>
                                        <Button classButton={"btn btn-danger"}>Delete</Button>
                                    </>
                            </td>
                        </tr>
                    })}
            </tbody>
        </table>
            </div>
            </div>
            </>
        )
        
                
    }
}
export default datauser;
