import React, { Component } from 'react';
import { Nav, Body, Footer } from "./templates"
import "./App.css"

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      defaultPass:"123",
      defaultType: 2,
      userListAPI:[],
      isLoggedIn: false,
      users:[]
    }
  }

  componentDidMount() {
    fetch('https://jsonplaceholder.typicode.com/users')
        .then(response => response.json())
        .then(json => this.setState({ users: json }))
}


  shouldComponentUpdate(lastProp) {
    if (lastProp.page !== this.state.page)
      return true
    return false
  }

  onClickButton = (page) => {
    this.setState({
      page
    })
  }

  changeLogIn = () => {
    this.setState(oldState => ({ isLoggedIn: !oldState.isLoggedIn }))
  }

  render() {
    console.log(this.state.page)
    return (
      <>
        <Nav
          statusLogin={this.state.isLoggedIn}
          changeLogIn={this.changeLogIn}
          changePage={this.onClickButton}
        />
        <Body
          statusLogin={this.state.isLoggedIn}
          changeLogIn={this.changeLogIn}
          page={this.state.page}
          changePage={this.onClickButton}
        />
        <Footer />
      </>
    );
  }
}

export default App;
