import React, { Component } from 'react';
import { Switch, Route } from "react-router-dom"
import "./style.css"
import {Daftar, Home, Login, Product} from '../../pages'


class Body extends Component {
    constructor(props) {
        super(props);
        this.state = {
            users: [],
        }
    }

    showPage = () => {
        const { changeLogIn, statusLogin } = this.props

        // if (page === "home")
        //     return <Home changePage={changePage} />

        // if (page === "about")
        //     return <About />

        // if (page === "login")
        //     return <Login changeLogIn={changeLogIn} listUsers={this.state.users} />

        // if (page === "register")
        //     return <Register listUsers={this.state.users} tambahUser={this.addUsers} />

        return (
            <Switch>
                <Route exact path="/">
                    <Home statusLogin={statusLogin} />
                </Route>
                <Route path="/product" component={Product} />
                <Route path="/login">
                    <Login changeLogIn={changeLogIn} listUsers={this.state.users} />
                </Route>
                <Route path="/daftar">
                    <Daftar listUsers={this.state.users} tambahUser={this.addUsers} />
                </Route>
            </Switch>
        )
    }

    addUsers = obj => {
        const { name, username, password } = obj
        let oldUsers = this.state.users
        oldUsers.push({
            name,
            username,
            password
        })
        this.setState({
            users: oldUsers
        })
    }

    render() {
        return (
            <>
                {
                    this.showPage()
                }
            </>
        );
    }
}
export default Body;
