import React, { Component } from 'react';

class datauser extends Component {
    constructor(props) {
        super(props);
        this.state = { 
            users:[]
        }
    }

    componentDidMount = async () => {
        // Fetching Api from Json Placeholder
        await fetch("http://localhost:3333/user")
          // await fetch("https://jsonplaceholder.typicode.com/users")
          .then((response) => response.json())
          .then((json) => {
            const dataUsers = json.map((user) => {
              return { ...user };
            });
            console.info("Data User: ", dataUsers);
            this.setState({
              users: [...dataUsers, ...this.state.admin],
            });
          });
      };
    render() { 
        return (
            <>
            <div className="listUser">
            <div className="container-fluid" style={{marginTop: "140px"}}>
            <h4>List Data User</h4>
            <table className="tableUser">
                <thead className="thead-dark">
                <tr>
                    <th>No</th>
                    <th>Nama</th>
                    <th>Email</th>
                    <th>Aksi</th>
                </tr>
                </thead>
            <tbody>
            {this.state.users.map((user, idx) => {  
                return <tr key={idx}>
                            <td scope="row" >{idx+1}</td>
                            <td>{user.email}</td>
                            <td>{user.name}</td>
                            <td> 
                                    <>
                                        <button className='btn btn sizefix'>Detail</button>
                                        <button className='btn btn-warning sizefix'> Edit</button>
                                        <button className='btn btn-danger sizefix'>Delete</button>
                                    </>
                                                            
                            </td>
                        </tr>
                    })}
            </tbody>
        </table>
            </div>
            </div>
            </>
        )
        
                
    }
}
 
export default datauser;
