import React, { Component } from 'react';
import { RowInput, Input } from '../../components';
import "./style.css"

class Daftar extends Component {
    constructor(props) {
        super(props);
        this.state = {
            name: "",
            username: "",
            password: "" 
        }
    }

    onChangeInput = e => {
        this.setState({
            [e.target.name]: e.target.value
        })
    }

    doDaftar = async () => {
        const { name, username, password } = this.state
        this.props.tambahUser({ name, username, password })
    }

    renderUsers = () => {
        return this.props.listUsers.map((users, idx) => {
            return <div key={idx}>
                name: {users.name} - password: {users.password}
            </div>
        })
    }


    render() {
        return (
            <div className="sectionDaftar">
                <div>
                    <RowInput value={this.state.name} label="Nama" type="text" name="name" onChange={this.onChangeInput} />
                    <RowInput value={this.state.username} label="Username" type="text" name="username" onChange={this.onChangeInput} />
                    <RowInput value={this.state.password} label="Password" type="password" name="password" onChange={this.onChangeInput} />
                    <div>
                        <Input typeInput="button" valueInput="Daftar" onClickInput={this.doDaftar} />
                    </div>
                </div>
                <div>
                    {this.renderUsers()}
                </div>
            </div>
        );
    }
}

export default Daftar;
